  /* 3 */
  -ms-text-size-adjust: 100%;
}



/*  Recent events
 *******************************/
#portfolio-categories-archive {
  position: relative;
  width: 100%;
  height: auto;
  max-width: none;
}
#portfolio-categories-archive article {
  width: 690px;
  margin: 0 auto -50px;
}
#portfolio-categories-archive article h2 {
  background: url(../img/custom_headline_portfolio.png) top left no-repeat;
  display: block;
  width: 224px;
  height: 86px;
  text-indent: -5555px;
  overflow: hidden;
  font-family: old_standard_ttitalic, Georgia, serif;
  color: #80c1c5;
  text-transform: capitalize;
  text-align: center;
  font-size: 72px;
  margin: 0 auto;
  letter-spacing: 2px;
  line-height: 1;
}
#portfolio-categories-archive a.open-event {
  margin: 40px auto 0 !important;
}
#recent-events-preview {
  background: url(img/clients/home-ropes.png) top center no-repeat;
  display: block;
  height: auto;
  overflow: hidden;
  padding: 80px 0;
  position: relative;
  width: 100%;
}
#recent-events-preview .wrapper {
  position: relative;
  margin: 0 auto;
  padding-top: 0px;
}
#recent-events-preview .flexslider {
  position: relative;
  width: 100%;
  height: auto;
}
#recent-events-preview .flexslider .slides li {
  position: relative;
  width: 100%;
  height: 460px;
  left:50px;
}
#recent-events-preview .flexslider .flex-direction-nav .flex-prev,
#recent-events-preview .flexslider .flex-direction-nav .flex-next {
  position: absolute;
  width: 43px;
  height: 87px;
  top: 0;
  bottom: 0;
  margin: auto;
  background-position: top left;
  background-repeat: no-repeat;
  text-indent: -9999px;
  overflow: hidden;
}
#recent-events-preview .flexslider .flex-direction-nav .flex-prev:hover,
#recent-events-preview .flexslider .flex-direction-nav .flex-next:hover {
  background-position: top right;
}
#recent-events-preview .flexslider .flex-direction-nav .flex-prev {
  
  background-image: url(img/clients/arrow_left-rev2.png);
}
#recent-events-preview .flexslider .flex-direction-nav .flex-next {
  right: -1px;
  background-image: url(img/clients/arrow_right-rev2.png);
}
#recent-events-preview .recent-event-preview {
  position: absolute;
  top: 0;
  width: 360px;
  height: 2px;
  padding: 0;
  overflow: visible;
  margin: 0;
  -webkit-transition: all;
  -moz-transition: all;
  -ms-transition: all;
  -o-transition: all;
  transition: all;
}
#recent-events-preview .recent-event-preview.hover {
  -webkit-animation: hover_event_preveiw 1.6s ease;
  /* Safari 4+ */
  -moz-animation: hover_event_preveiw 1.6s ease;
  /* Fx 5+ */
  -o-animation: hover_event_preveiw 1.6s ease;
  /* Opera 12+ */
  animation: hover_event_preveiw 1.6s ease;
  /* IE 10+ */
}
#recent-events-preview .recent-event-preview:before {
  background: url(img/clients/home-rope-ties.png) 50% 0 no-repeat;
  content: " ";
  display: block;
  width: 40px;
  height: 62px;
  position: absolute;
  left: 150px;
}
#recent-events-preview .recent-event-preview .box {
  background: url(img/clients/home-event-thumbbg.png) 50% 0 no-repeat;
  width: 300px;
  height: 328px;
  padding: 28px 30px;
}
#recent-events-preview .recent-event-preview#event-1 {
  top: 53px;
  left: 15px;
}
#recent-events-preview .recent-event-preview#event-1:before {
  background-position: 0 0;
  top: -30px;
}
#recent-events-preview .recent-event-preview#event-2 {
  top: 77px;
  left: 362px;
}
#recent-events-preview .recent-event-preview#event-2:before {
  background-position: -40px 0;
  top: -38px;
}
#recent-events-preview .recent-event-preview#event-3 {
  top: 33px;
  left: 712px;
}
#recent-events-preview .recent-event-preview#event-3:before {
  background-position: -80px 0;
  top: -30px;
}
#recent-events-preview .recent-event-preview figure {
  width: 288px;
  height: 192px;
  display: block;
  overflow: hidden;
  margin: 0 0 10px 2px;
  -webkit-transform: rotate(-0.6deg);
  -moz-transform: rotate(-0.6deg);
  transform: rotate(-0.6deg);
}
#recent-events-preview .recent-event-preview figure img {
  width: 288px;
  height: auto;
}
#recent-events-preview .recent-event-preview h4 {
  display: table;
  font-family: 'BrandonGrotesqueBlackRegular', Arial, Helvetica, sans-serif;
  font-size: 13px;
  text-transform: uppercase;
  width: 288px;
  margin: 20px auto 10px;
  height: 30px;
  text-align: center;
}
#recent-events-preview .recent-event-preview h4 a {
  color: #333333;
  display: table-cell;
  vertical-align: middle;
  height: 30px;
}
#recent-events-preview .recent-event-preview h4 a:hover {
  color: #80c1c5;
}
#recent-events-preview .recent-event-preview a.event-cat {
  color: #b7b0b0;
  display: block;
  font-family: 'BrandonGrotesqueBlackRegular', Arial, Helvetica, sans-serif;
  font-size: 11px;
  text-transform: uppercase;
  width: 288px;
  margin: 10px auto;
  text-align: center;
}
#recent-events-preview .recent-event-preview a.event-cat:hover {
  color: #191919;
}
#recent-events-preview .recent-event-preview a.open-event {
  background: url(../img/simple-arrows.png) 100% -24px no-repeat;
  color: #80c1c5;
  display: block;
  font-family: 'BrandonGrotesqueBlackRegular', Arial, Helvetica, sans-serif;
  font-size: 10px;
  text-transform: uppercase;
  padding: 0 22px 0 0;
  width: 288px;
  margin: 21px auto 0;
  text-align: center;
  width: 28px;
}
#recent-events-preview .recent-event-preview a.open-event:hover {
  background-position: 100% -4px;
  color: #191919;
}






