  margin: 10px auto;
  text-align: center;
}
.recent-event-preview .event-location:hover,
.recent-event-preview a.event-cat:hover {
  color: #191919;
}
.recent-event-preview a.open-event {
  background: url(../img/simple-arrows.png) 100% -24px no-repeat;
  color: #80c1c5;
  display: block;
  font-family: 'BrandonGrotesqueBlackRegular', Arial, Helvetica, sans-serif;
  font-size: 10px;
  text-transform: uppercase;
  padding: 0 22px 0 0;
  width: 288px;
  margin: 21px auto 0;
  text-align: center;
  width: 28px;
}
.recent-event-preview a.open-event:hover {
  background-position: 100% -4px;
  color: #191919;
}
.recent-event-preview .no-location h4 {
  margin-top: 50px;
}
.recent-event-preview .no-location a.open-event {
  margin-top: 14px;
}

/* Mobile menu
*******************************/

/* Select
*******************************/


@media (max-width: 640px) {
  #header-main .social {
    top: -10px;
  }
  
  
  
  
 
 
  
  #services article {
    width: 100%;
  }
  #services article h2 {
    background-size: 100% auto;
    width: 70%;
    min-height: 85px;
    margin: 0 auto;
  }
  #services #services-bottom {
    width: 90%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    -o-box-sizing: border-box;
    box-sizing: border-box;
  }
  #services #services-bottom #service-info .options-list {
    -moz-column-count: 1;
    -moz-column-gap: 0;
    -webkit-column-count: 1;
    -webkit-column-gap: 0;
    column-count: 1;
    column-gap: 0;
  }
  #services-preview,
  #recent-events-preview {
    background: none;
    display: block;
    min-height: auto;
    height: auto;
    overflow: hidden;
    padding: 20px 0;
    position: relative;
    width: 100%;
  }
  #services-preview .flexslider .slides li,
  #recent-events-preview .flexslider .slides li {
    display: block;
    height: auto !important;
  }
  #services-preview .wrapper,
  #recent-events-preview .wrapper {
    position: relative;
    width: 100%;
    max-width: 100%;
  }
  #services-preview .wrapper #event-2,
  #recent-events-preview .wrapper #event-2 {
    display: block;
  }
  #services-preview article,
  #recent-events-preview article {
    width: 360px;
    margin: 0 auto;
  }
  #services-preview article h3,
  #recent-events-preview article h3 {
    background: url(../img/illst-recentevents-headline.png) 0 0 no-repeat;
    display: block;
    width: 359px;
    height: 264px;
    text-indent: -4545px;
  }
  #services-preview article .content,
  #recent-events-preview article .content {
    font-size: 16px;
  }
  #services-preview .service-preview,
  #recent-events-preview .service-preview,
  #services-preview .recent-event-preview,
  #recent-events-preview .recent-event-preview {
    position: relative;
    width: 360px;
    height: 368px;
    top: auto !important;
    left: auto !important;
    right: auto !important;
    margin: 0 auto;
    float: none;
    padding: 0 !important;
  }
  #services-preview .service-preview:before,
  #recent-events-preview .service-preview:before,
  #services-preview .recent-event-preview:before,
  #recent-events-preview .recent-event-preview:before {
    content: "";
    display: none;
  }
  #services-preview .service-preview .box,
  #recent-events-preview .service-preview .box,
  #services-preview .recent-event-preview .box,
  #recent-events-preview .recent-event-preview .box {
    background: url(img/clients/home-event-thumbbg.png) 50% 0 no-repeat;
    width: 300px;
    height: 328px;
    padding: 28px 30px;
  }
  
 
  
}





